events.inlineCode:
//Scene Name | runtimeScene._name
//Hight      | runtimeScene._runtimeGame._gameResolutionHeight
//Width      | runtimeScene._runtimeGame._gameResolutionWidth
//is Paused  | runtimeScene._runtimeGame._paused
//Scaleing   | runtimeScene._runtimeGame._scaleMode
//RunTime    | runtimeScene._timeManager.getElapsedTime()

var debugInfo = "Debug: {'Scene Name' : '"+runtimeScene._name.toString()+"', 'Version' : '"+runtimeScene._runtimeGame._data.variables[4].value.toString()+"', 'WinHight' : '"+runtimeScene._runtimeGame._gameResolutionHeight.toString()+
"', 'WinWidth' : '"+runtimeScene._runtimeGame._gameResolutionWidth.toString()+"', 'isPaused' : '"+runtimeScene._runtimeGame._paused.toString()+
"', 'Scaleing' : '"+runtimeScene._runtimeGame._scaleMode.toString()+"', 'RunTime' : '"+Math.round(runtimeScene._timeManager.getTimeFromStart()).toString()+"'}";

let date_ob = new Date();
let date = ("0" + date_ob.getDate()).slice(-2);
let month = ("0" + (date_ob.getMonth() + 1)).slice(-2);
let year = date_ob.getFullYear();
let hours = date_ob.getHours();
let minutes = date_ob.getMinutes();
let seconds = date_ob.getSeconds();
let ms = date_ob.getMilliseconds();

runtimeScene.getVariables().get("debugInfo").setString(debugInfo);
runtimeScene.getVariables().get("dateTime").setString(year + "-" + month + "-" + date + "_" + hours + "-" + minutes + "-" + seconds + "-" + ms);
